    height: 111px;
  }

  @include mobileOnly {
    padding-top: 0.7rem;
    flex-direction: column;
  }
}

.navbar__content {
  display: flex;
  flex-direction: row;
  flex-basis: 37%;
  gap: 1rem;
  align-items: center;
  justify-content: space-between;

  @include mobileOnly {
    gap: 0;
  }
}

.navbar__list {
  padding-bottom: 1rem;
  display: flex;
  align-items: center;
  height: 50px;
}
.navbar__item {
  display: flex;
  height: 100%;
  align-items: center;
  padding: 0 1.35rem;
  border-bottom: 2px solid transparent;

  &:hover {
    border-bottom: 2px solid $border-color;
    cursor: pointer;

    @include tabletOnly {
      border-bottom: 2px solid transparent;
    }
    @include mobileOnly {
      border-bottom: 2px solid transparent;
    }

    a {
      color: $main-color;
    }
  }

  @include mobileOnly {
    font-size: $font-size-xs;
    padding: 0 0.8rem;

    &:first-child {
      padding-left: 0;
    }
  }
}

.navbar__sublist__item {
  padding: 0.7rem 0.3rem;
  border-bottom: 1px solid $text-links;
  cursor: pointer;

  &:last-child {
    border-bottom: none;
  }

  &:hover a {
    color: $text-subheader;
  }
}

