  z-index: 1;
  top: 46%;
  @include desktopOnly {
    right: 2.6%;
  }
  @include absFix {
    right: 3%;
  }
  @include tabletOnly {
    right: 5%;
  }
  @include mobileOnly {
    right: 11%;
  }

  &::before {
    font-size: 35px;
    font-weight: bold;
    padding: 0 1rem 0.8rem 1rem;
