.contact {
  position: relative;
  background-image: linear-gradient(to left, $secondary-color, $dark-color);
}

.contact-wrap {
  display: flex;
  padding: 7rem 0 1rem 0;
  z-index: 1;
  justify-content: space-between;

  @include tabletOnly {
    padding: 2rem;
  }
  @include mobileOnly {
    flex-direction: column;
  }
}

.contact__left {
  z-index: 3;

  @include desktopOnly {
    flex-basis: 50%;
  }
  @include tabletOnly {
    flex-basis: 40%;
  }
}

.contact__subheading {
  @extend .banner__subheading;
}

.contact__heading {
  color: $main-color;

  @include desktopOnly {
    font-size: $font-size-xl;
  }
  @include tabletOnly {
    font-size: $font-size-l;
  }
  @include mobileOnly {
    font-size: $font-size-basic-m;
    line-height: 1.4;
    text-align: center;
    padding-bottom: 1rem;
  }
}

.contact__form-wrap {
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: $contact-color;
  padding: 3rem 0.5rem 4rem 3rem;
  @include desktopOnly {
    flex-basis: 42%;
  }
  @include tabletOnly {
    flex-basis: 55%;
  }
  @include mobileOnly {
    padding: 2rem 0 2rem 2rem;
  }
}

.contact__form {
  flex-basis: 100%;
}
// Custom placeholder

.input-placeholder {
  position: relative;
}
.input-placeholder input {
  padding: 1.3rem 1rem;

  font-size: $font-size-basic;
  color: $main-color;
  font-family: $font-roboto;
  width: 90%;
  margin-bottom: 2rem;
  background: rgba($main-color, 0.1);
  border: 1px solid $text-links;
  border-radius: 5px;

  @include mobileOnly {
    margin-bottom: 1rem;
  }
}
.input-placeholder textarea {
  padding: 10px;
  font-size: $font-size-basic;
  color: $main-color;
  font-family: $font-roboto;
  width: 90%;
  resize: none;
  background: rgba($main-color, 0.1);
  border: 1px solid $text-links;
}
.input-placeholder input:valid + .placeholder {
  display: none;
}
.input-placeholder textarea:valid + .placeholder {
  display: none;
}

.placeholder {
  font-size: $font-size-basic;
  font-family: $font-roboto;
  position: absolute;
  pointer-events: none;
  top: 0;
  bottom: 1rem;
  height: 35px;
  left: 16px;
  margin: auto;
  color: #ccc;
  padding-left: 0.2rem;
}

.placeholder--textarea {
  margin-top: 3%;
}

.placeholder span {
  color: red;
}

.contact__form__btn {
  font-family: $font-roboto;
  font-size: $font-size-basic;
  background-color: $contact-color;
  color: $main-color;
  border: 2px solid $text-links;
  border-radius: 3px;
  padding: 1rem 2.3rem;
  margin: 2rem auto auto 20%;
  transition: 0.2s all ease-in;

  &:hover {
    cursor: pointer;
    background-color: rgba($main-color, 0.2);
  }

  @include mobileOnly {
    margin-top: 1rem;
    margin: 2rem auto auto auto;
  }
}

.contact__image {
  position: absolute;
  left: -14%;
  z-index: 2;
  top: 36%;

  @include absFix {
    left: -28%;
  }
  @include tabletOnly {
    left: -22%;
    width: 75%;
  }
  @include mobileOnly {
    display: none;
  }
}

// Footer

.footer {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 2rem 3rem;
  font-size: $font-size-s;
  font-family: $font-roboto;

  @include desktopOnly {
    gap: 10rem;
  }
  @include tabletOnly {
    gap: 2rem;
  }
  @include mobileOnly {
    padding: 0 2rem 1.5rem 2rem;
    flex-direction: column;
  }
}

.footer__left {
  color: $text-links;
  @include desktopOnly {
    padding-left: 6rem;
  }
  @include tabletOnly {
    padding-left: auto;
  }
}

.footer__right {
  display: flex;
  gap: 3rem;

  @include mobileOnly {
    gap: 2rem;
  }
}

.footer__link {
  &:hover {
    color: $text-subheader;
  }
}
